### Compute the number of times of pattern appears in a text ###
def PatternCount(text,pattern):
    count= 0
    for i in range(len(text)-len(pattern)+1):
        if text[i:i+len(pattern)] == pattern:
            count += 1
    return count

text = ''
pattern = 'ATGTCCCAT'
print(PatternCount(text,pattern))