### Find substrings of a genome encoding a given amino acid string ###
from BA4A import Translation

def Encode(text,peptide):
    dnas = [text, ReverseComplement(text)]
    substrings = []

    for dna in dnas:
        l = len(peptide)*3
        for i in range(len(dna)-l+1):
            substring = dna[i:i+l]
            if Translation(Transcription(substring)) == peptide:
                if dna == text:
                    substrings.append(substring)
                else:
                    substrings.append(ReverseComplement(substring))
    return substrings

def Transcription(text):
    t_text= ''
    for i in range(len(text)):
        if text[i] == 'T':
            base = 'U'
        else:
            base = text[i]
        t_text += base
    return t_text

def ReverseComplement(text):
    basepair = {'A':'T', 'C':'G', 'G':'C', 'T':'A'}
    rc_text = ''
    for base in text:
        rc_text += basepair[base]
    return rc_text[::-1]

dnaString = ''
peptide = 'NIGWWMYA'

answer = ''
for substring in Encode(dnaString,peptide):
    answer += substring + '\n'
print(answer)